import React,{useEffect,useState} from "react";
import { useFormik} from "formik";
import moment from 'moment';
 import {useParams} from "react-router-dom";
import * as Yup from "yup";
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import EditIcon from '@material-ui/icons/Edit';
import VisibilityIcon from '@material-ui/icons/Visibility';
import Link from "@material-ui/core/Link";
import {addTodo, readTodo, setHolidays} from '../slice/holidaySlice';
//import {setForID,setHolidays} from '../slice/holidaySlice';
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
// import moment from "moment";
import { holidayListSliceState,holidayListSliceId} from "../slice/holidaySlice";
import DeleteIcon from '@material-ui/icons/Delete';
import {useHistory} from "react-router-dom";
import axios from "axios";


const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),

  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};
const hType =(code) =>{
  let desc1="Fixed"

  switch (code) {
    case "F":
        desc1="Fixed"
        break;
     case "O":
         desc1 ="Options"
         break;     
    }
  return desc1;
}

const location1 = (code) =>{
  let desc=  "All Locations";

  switch (code) {
     case "ALL":
         desc ="All Locations"
         break;
      case "IND":
          desc ="India"
          break;  
      case "USA":
         desc ="Bettendorf, IA"
         break;
       }
  return desc;
}

export default function App() {
  const classes = useStyles();
  const dispatch = useDispatch();

   const holidays = useSelector(holidayListSliceState);

   const holidaysListSliceId=useSelector(holidayListSliceId);
   console.log("formikvalues1234",holidaysListSliceId.date);

    let  {id ,actionType}= useParams();
    console.log("actionType",actionType);


    useEffect(()=>{
       if(actionType === "view"){
    dispatch(readTodo(id))
       }
  },[id])
 //console.log("id",holidays.HolidayId)

  const formik = useFormik({
   initialValues: {
     id:"",
      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false

    },
   validationSchema: Yup.object({
      date:Yup.string().required("Required!").nullable(),
      location:Yup.string().required("Required!").nullable(),
      type:Yup.string().required("Required!").nullable(),
      description:Yup.string().required("Required!").nullable(),
    }),

    onSubmit:values => {

      console.log("values",values);
         dispatch(addTodo(values));
          // dispatch(readTodo(id));
        console.log("formikvalues1234",holidays.date)
                //  moment((formik.values.date,'YYYY/mm/dd').format('mm/dd/YYYY')),
                // hType(formik.values.type),
                // location1(formik.values.location),
        //  )
         //dispatch(setForId(id));

       alert(JSON.stringify(values, null, 2));

     }})

  return (
    <div className="App">

         <form  onSubmit={formik.handleSubmit} >

                <Box display="flex" justifyContent="center" border={1} {...defaultProps}  px={2} pt={5} pb={4}>
                          <Grid container spacing={3}>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                              name="HolidayId"
                                id="outlined-full-width"
                                placeholder="#"
                               value={holidaysListSliceId.HolidayId}
                               onChange={formik.handleChange}

                                style={{ margin: 1 }}

                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"
                                disabled
                              />
                              {formik.errors.HolidayId && formik.touched.HolidayId && (
                                <p>{formik.errors.HolidayId}</p>
                              )}
                            </Grid>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                             <TextField
                              id="date"
                              name="date"
                              type="date"
                              //value="2021/01/01"
                               value={moment(holidaysListSliceId.date,'MM-DD-YYYY').format('YYYY-MM-DD')}
                              onChange={formik.handleChange}

                             style={{ margin: 1 }}
                             dateFormat="MM/DD/YYYY"
                              fullWidth
                              margin="normal"
                              InputLabelProps={{
                                shrink: true,
                              }}
                              variant="outlined"
                            />

                            {formik.errors.date && formik.touched.date && (
                              <p>{formik.errors.date}</p>
                            )}
                           </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="location" className={classes.inputLabel} >Location:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="location"
                                name="location"
                                type="text"

                                  value= {location1(holidaysListSliceId.location)}
                                 onChange={formik.handleChange}

                                select

                                SelectProps={{
                                  native: true,
                                }}
                                style={{ marginRight: 1 }}
                                variant="filled"
                                fullWidth

                              >
                                {location.map((option) => (
