import React,{useEffect,useState} from "react";
import { useFormik} from "formik";
 import {useParams} from "react-router-dom";
import * as Yup from "yup";
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
// import DeleteIcon from '@material-ui/icons/Delete';
import Link from "@material-ui/core/Link";
import {addTodo, getHolidays, readTodo} from '../slice/holidaySlice';
import {setForID,setHolidays} from '../slice/holidaySlice';
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
// import moment from "moment";
import { holidayListSliceState1,holidayListSliceState} from "../slice/holidaySlice";
import DataFetching from "./DataFetching";
import DeleteIcon from '@material-ui/icons/Delete';
// import { render } from "@testing-library/react";
import actionType from '../router/Router';



const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};

export default function App() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const holidays = useSelector(holidayListSliceState);
  const dataforId = useSelector(holidayListSliceState1);
  const[todoComplete,setTodoComplete] = useState([]);
     console.log("dataforId",dataforId );
    let { id }= useParams();
    const [getHolidayById,setHolidays]= useState([]);
      console.log("123456",getHolidayById)




     useEffect(()=>{
    dispatch(readTodo(id))
  },[id])

  useEffect(()=>{
    const singleHoliday = getHolidayById.holidays[0]
        console.log("789000",singleHoliday)
    setHolidays(dataforId)
  },[dataforId])

  const formik = useFormik({
   initialValues: {
       id:"",
      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false

    },
   validationSchema: Yup.object({
      date:Yup.string().required("Required!"),
      location:Yup.string().required("Required!").nullable(),
      type:Yup.string().required("Required!").nullable(),
      description:Yup.string().required("Required!").nullable(),
    }),
    onSubmit: values => {
      // console.log(props,"props")
      console.log("values",values);
         dispatch(addTodo(values));

       alert(JSON.stringify(values, null, 2));

    },
  })

  return (
    <div className="App">

         <form  onSubmit={formik.handleSubmit} >

                <Box display="flex" justifyContent="center" border={1} {...defaultProps}  px={2} pt={5} pb={4}>
                          <Grid container spacing={3}>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                              name="HolidayId"
                                id="outlined-full-width"
                                placeholder="#"
                               //value={singleHoliday.HolidayId}
                                style={{ margin: 1 }}

                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"
                                disabled
                              />
                            </Grid>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                             <TextField
                              id="date"
                              name="date"
                              type="date"
                              onChange={formik.handleChange}

                              style={{ margin: 1 }}
                              format="MM/dd/yyyy"
                              fullWidth
                              margin="normal"
                              InputLabelProps={{
                                shrink: true,
                              }}
                              variant="outlined"
                            />

                            {formik.errors.date && formik.touched.date && (
                              <p>{formik.errors.date}</p>
                            )}
                           </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="location" className={classes.inputLabel} >Location:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="location"
                                name="location"
                                type="text"
                               onChange={formik.handleChange}


                                select

                                SelectProps={{
                                  native: true,
                                }}
                                style={{ marginRight: 1 }}
                                variant="filled"
                                fullWidth

                              >
                                {location.map((option) => (
                                  <option key={option.value} value={option.value}>
                                    {option.label}
                                  </option>
                                ))}

                              </TextField><br></br>
                              {formik.errors.location && formik.touched.location && (
                                <p>{formik.errors.location}</p>
                              )}
                            </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="type" className={classes.inputLabel} >Holiday Type:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="type"
                                name="type"
                                type="text"
                               onChange={formik.handleChange}
                               value={formik.values.type}

                                select
                                style={{ marginRight: 1 }}

                                SelectProps={{
                                  native: true,
                                }}
                                variant="filled"
                                fullWidth

                              >
                                {type.map((option) => (
                                  <option key={option.value} value={option.value}>
                                    {option.label}
                                  </option>
                                ))}

                              </TextField>
                              {formik.errors.type && formik.touched.type&& (
                                <p>{formik.errors.type}</p>
                              )}
                            </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="description" className={classes.inputLabel} >Description:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="description"
                                name="description"
                                type="text"
                                onChange={formik.handleChange}


                                style={{ marginRight: 1 }}
                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"

                              />
                              {formik.errors.description && formik.touched.description && (
                                <p>{formik.errors.description}</p>
                              )}
                            </Grid>

                            <Grid item xs={12} sm={12} md={4} lg={4}
                              style={{
                                textAlign: 'center'
                              }}
                            >
                              <Button>

                              </Button>
                            </Grid>
                             <Link href="/create/">
                              <Button
                                variant="contained"
                                color="primary"
                                style={{ margin: '2 auto', display: "flex" }}
                                type="submit"
                                startIcon={<SaveIcon />}

                              >
                                Save
                              </Button>
                              </Link>
                             <Link href='/'>
                              <Button variant="contained" 
                              style={{ margin: '3 auto', display: "flex" }}

                              >Goto:Holidays
                              </Button>
                              </Link>

                              {/*<Link href="/view/">

                              <Button
                                variant="contained"
                                color="primary"
                                style={{ margin: '2 auto', display: "flex" }}
                                type="submit"
                                startIcon={<SaveIcon />}

                              >
                              Switch To Update
                              </Button>

                              </Link>


                              <Link href="/update/">
                              <Button variant="contained" 
                              style={{ margin: '3 auto', display: "flex" }}
                              startIcon={<DeleteIcon />}

                              >Delete
                              </Button>
                              </Link>
                              <Link href="/update/">
                              <Button variant="contained" 
                              style={{ margin: '3 auto', display: "flex" }}


                              >Switch To View
                              </Button>
                            </Link>*/}





                         </Grid>

                   </Box>

                  </form>


                     </div>


  )}
