        value={currency}
        onChange={handleChange}
        SelectProps={{
          native: true,
        }}

        variant="filled"
      >
      {type.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}

      </TextField><br></br>


        <InputLabel htmlFor="Description">Description:</InputLabel>
        <TextField id="outlined-basic" label="Outlined" variant="outlined" style ={{width: '170%'}}/><br></br>
        <button type="submit"  color="blue">Save</button>
        <button type="Goto holidays">Goto:holidays</button>
        </Form>
      </Box>
    </Formik>
    </Container>
    </Grid>

)}


export default Create;
