import React from 'react';
import { Formik, Field, Form} from 'formik';
import * as yup from 'yup';
import { makeStyles} from '@material-ui/core';
import Grid from "@material-ui/core/Grid"; 
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import Input from '@material-ui/core/Input';
import moment from "moment";
import { borders } from '@material-ui/system';
import NavBar from './NavBar';
import TextField from '@material-ui/core/TextField';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
const  useStyles = makeStyles((theme) => ({
formWrapper :{
  marginTop:theme.spacing(5),
  marginBottom:theme.spacing(8),
},
}))
const defaultProps = {
  bgcolor: 'background.paper',
  m: 2,
  border: 2,
  style: { width: '70rem', height: '30rem' },
};
const INTIAL_FORM_STATE = {
   HOLIDAY_Id:'',
   DATE:'',
   Type:'',
   Location:'',
   Description:''
};
const validationSchema =yup.object({

})

function Create ()  {
   const classes = useStyles();
   const [anchorEl, setAnchorEl] = React.useState(null);
   const [values, setValues] = React.useState();
   const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };
  return(

    <Grid container>


    <Container>
    <div classname={classes.formWrapper}></div>
      <Formik 
       intialValue ={{
         ...INTIAL_FORM_STATE
       }}
       validationSchema
       onSubmit ={values => {
         console.log(values);
       }}
      >
      <Box display="flex" justifyContent="center" border={1} {...defaultProps} >
      <Form>
      <FormControl fullWidth className={classes.margin}>
      <InputLabel htmlFor="standard-adornment-amount">Amount</InputLabel>
      <Input
        id="standard-adornment-amount"
        value={values.amount}
        onChange={handleChange('amount')}
        startAdornment={<InputAdornment position="start">$</InputAdornment>}
      />
    </FormControl>
        <label htmlFor="Date">Date:</label>
        <Field id="Date" name="Date" /><br></br>
        <label htmlFor="Holiday Type">Holiday Type:</label>
        <Field name="Type" as="select">
        <option value="Fixed">Fixed</option>
        <option value="Optional">optional</option>
        </Field><br></br>
        <label htmlFor="Location">Location:</label>
        <Field name="Location" as="select">
        <option value="All Locations">All Locations</option>
        <option value="Bettendorf,IA">Bettendorf,IA</option>
        <option value="India">India</option>
      </Field><br></br>
        <label htmlFor="Description">Description:</label>
        <Field id="Description" name="Description" /><br></br>
        <button type="submit">Save</button>
        <button type="Goto holidays">Goto:holidays</button>
      </Form>
      </Box>
    </Formik>
    </Container>
    </Grid>

)}


export default Create;
