    return(

        <Formik
            intial Values={{
                date:"",
                description:"",
                type:"",
                location:""
            },
            validationSchema= Yup.object({
                date:Yup.string().required("Required!").nullable(),
                location:Yup.string().required("Required!").nullable(),
                type:Yup.string().required("Required!").nullable(),
                description:Yup.string().required("Required!").nullable(),
              }),
            onSubmit =(values)=>{
            console.log({values})
        }}
        >
        {({handleSubmit,handleChange,onBlur}) =>{
            return (
            <form onSubmit={{handleSubmit}}>
               <input name="HolidayId"
               type="HolidayId"
               placeholder="#"
               onChange={handleChange}
               onBlur={handleBlue}
                />
            </form>
            )
        }}
        </Formik>






    )
}
