import React from 'react';
import { Formik, Field, Form} from 'formik';
import * as yup from 'yup';
import { makeStyles} from '@material-ui/core';
import Grid from "@material-ui/core/Grid"; 
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import moment from "moment";
import { borders } from '@material-ui/system';
import NavBar from './NavBar';
const  useStyles = makeStyles((theme) => ({
formWrapper :{
  marginTop:theme.spacing(5),
  marginBottom:theme.spacing(8),
},
}))
const defaultProps = {
 bgcolor: 'white',
  m: 3,
  border: 120,
  style: { width: '50rem', height: '20rem' },
};
const INTIAL_FORM_STATE = {
   HOLIDAY_Id:'',
   DATE:'',
   Type:'',
   Location:'',
   Description:''
};
const validationSchema =yup.object({

})

function Create ()  {
   const classes = useStyles();
   const [anchorEl, setAnchorEl] = React.useState(null);
  return(

    <Grid container>


    <Container>
    <div classname={classes.formWrapper}></div>
      <Formik 
       intialValue ={{
         ...INTIAL_FORM_STATE
       }}
       validationSchema
       onSubmit ={values => {
         console.log(values);
       }}
      >
      <Box display="flex" justifyContent="center" >
      <Form>
        <label htmlFor="Holiday Id">Holiday Id:</label>
        <Field id="HolidayId" name="HolidayId" placeholder="#" />
        <label htmlFor="Date">Date:</label>
        <Field id="Date" name="Date" />
        <label htmlFor="Holiday Type">Holiday Type:</label>
        <Field name="color" as="select">
        <option value="Fixed">Fixed</option>
        <option value="Optional">optional</option>

      </Field>
        <label htmlFor="Location">Location:</label>
        <Field id="Location" name="Location" />
        <label htmlFor="Description">Description:</label>
        <Field id="Description" name="Description" />
        <button type="submit">Save</button>
        <button type="Goto holidays">Goto:holidays</button>
      </Form>
      </Box>
    </Formik>
    </Container>
    </Grid>

)}


export default Create;
