import { TableRow, Table, TableCell, TableBody ,Box, IconButton} from '@material-ui/core';
import React, { useEffect } from 'react';
import { fetchUsers, holidayListSliceState } from '../slice/holidaySlice';
import { useSelector } from 'react-redux';
import { makeStyles,withStyles } from '@material-ui/core/styles';
import moment from 'moment';
import { useDispatch } from 'react-redux';
import Typography from '@material-ui/core/Typography';
import { createTheme,ThemeProvider} from '@material-ui/core/styles';
import { Link } from 'react-router-dom';
import VisibilityIcon from '@material-ui/icons/Visibility';
import red from '@material-ui/core/colors/red';
import EditIcon from '@material-ui/icons/Edit';
const useStyles = makeStyles({

  tableCell: {
    padding: "16px 16px"
  },
    tableRow: {
      height: '40%'
    },
  })
  const dropdownStyles = makeStyles({
    underline: {
      borderBottom: "0px ",
      "&:hover": {
        borderBottom: "0px "
      },
    }
  });
 const RedTextTypography = withStyles({
  root: {
    color: "#ba000d"
  }
})(Typography);

const theme = createTheme({
  typography: {
    fontFamily: 'Raleway, Arial',
    fontSize: 16,

  },
})
//fetching the data from the api
function DataFetching() {
  const classes = useStyles();
  const holidays = useSelector(holidayListSliceState);
  const currentYear = moment().format('YYYY');
  const today = parseInt(currentYear);
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(fetchUsers(today))
  }, [today])

  return (
    <ThemeProvider theme={theme}>
    <React.Fragment>

    { holidays.holidays && holidays.holidays.length >0 ?
      <Table  aria-label="simple table" size="small"  >
        <TableBody>
      { holidays.holidays.map(holiday => (
           <TableRow  className={classes.tableRow} variant="footer" fontWeight="fontWeightBold"  >
           <TableCell >
           <Link href='/view'></Link>
           <IconButton >
           <VisibilityIcon  style={{ borderBottom: 'none' }} className={classes.underline} />
           </IconButton>
           <IconButton>
           <EditIcon />
           </IconButton>
           </TableCell>
           <TableCell className={classes.tableCell} style={{borderBottom: 'none'}}>{holiday.Date}</TableCell>
           <TableCell  style={{borderBottom: 'none'}}>{holiday.Description}</TableCell>
           <TableCell  style={{borderBottom: 'none',paddingLeft:'1%'}}>{holiday.Type}</TableCell>
           <TableCell  style={{ borderBottom: 'none' ,paddingLeft:'0%'}}> {holiday.Location}</TableCell>
         </TableRow>

      ))}
          </TableBody>
          </Table>
       :

          <Box mt={5}  paddingLeft={70} >
          <RedTextTypography variant="h6">
          No Records Found for this particular year
        </RedTextTypography>

            </Box>

      } 
      </React.Fragment>
      </ThemeProvider>
  );

}

export default DataFetching;
