import React,{useEffect,useState} from "react";
import { useFormik} from "formik";
import moment from 'moment';
 import {useParams} from "react-router-dom";
import * as Yup from "yup";
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
// import DeleteIcon from '@material-ui/icons/Delete';
import Link from "@material-ui/core/Link";
import {addTodo, readTodo} from '../slice/holidaySlice';
//import {setForID,setHolidays} from '../slice/holidaySlice';
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
// import moment from "moment";
import { holidayListSliceState1,holidayListSliceState} from "../slice/holidaySlice";
import DeleteIcon from '@material-ui/icons/Delete';
// import { render } from "@testing-library/react";
import actionType from '../router/Router';



const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};
const hType =(code) =>{
  let desc1="Fixed"

  switch (code) {
    case "F":
        desc1="Fixed"
        break;
     case "O":
         desc1 ="Options"
         break;     
    }
  return desc1;
}

const location1 = (code) =>{
  let desc=  "All Locations";

  switch (code) {
     case "ALL":
         desc ="All Locations"
         break;
      case "IND":
          desc ="India"
          break;  
      case "USA":
         desc ="Bettendorf, IA"
         break;
       }
  return desc;
}

export default function App() {
  const classes = useStyles();
  const dispatch = useDispatch();

  const holidays = useSelector(holidayListSliceState);
  //const dataforId = useSelector(holidayListSliceState1);
  //const[todoComplete,setTodoComplete] = useState();
     //console.log("dataforId",dataforId );
    let  {id }= useParams();

    const [getHolidayById,setHolidays]= useState([]);
       console.log("123456",getHolidayById)

    useEffect(()=>{
    dispatch(readTodo(id))
  },[id])

  // useEffect(()=>{
  //   const singleHoliday = getHolidayById.holidays[0]
  //       console.log("789000",singleHoliday)
  //   setHolidays(dataforId)
  // },[dataforId])

  const formik = useFormik({
   initialValues: {
      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false

    },
   validationSchema: Yup.object({
      date:Yup.string().required("Required!").nullable(),
      location:Yup.string().required("Required!").nullable(),
      type:Yup.string().required("Required!").nullable(),
      description:Yup.string().required("Required!").nullable(),
    }),
    // onSubmit:(values,{setSubmitting}) => {
    //   const errors={};
    //   console.log("values",values);
    //      dispatch(addTodo(values));

    //      //dispatch(setTodo(id));

    //    alert(JSON.stringify(values, null, 2));
    //   setSubmitting(false);
    // }})
    onSubmit:values => {

      console.log("values",values);
         dispatch(addTodo(values));
          dispatch(readTodo(id));
         console.log("formikvalues1234",
                 formik.values.description,
                 moment((formik.values.date,'YYYY/mm/dd').format('mm/dd/YYYY')),
                hType(formik.values.type),
                location1(formik.values.location),
         )
         //dispatch(setForId(id));

       alert(JSON.stringify(values, null, 2));

     }})

  return (
    <div className="App">

         <form  onSubmit={formik.handleSubmit} >

                <Box display="flex" justifyContent="center" border={1} {...defaultProps}  px={2} pt={5} pb={4}>
                          <Grid container spacing={3}>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                              name="HolidayId"
                                id="outlined-full-width"
                                placeholder="#"
                               //value={formik.values.holidayId}
                                style={{ margin: 1 }}

                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"
                                disabled
                              />
                              {formik.errors.HolidayId && formik.touched.HolidayId && (
                                <p>{formik.errors.HolidayId}</p>
                              )}
                            </Grid>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                             <TextField
                              id="date"
                              name="date"
                              type="date"
                              onChange={formik.handleChange}
                             //value={moment(formik.values.date,'YYYY-MM-DD').format('MM-DD-YYYY')}

                             style={{ margin: 1 }}
                              dateFormat="MM/dd/yyyy"
                              fullWidth
                              margin="normal"
                              InputLabelProps={{
                                shrink: true,
                              }}
                              variant="outlined"
                            />

                            {formik.errors.date && formik.touched.date && (
                              <p>{formik.errors.date}</p>
                            )}
                           </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="location" className={classes.inputLabel} >Location:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="location"
                                name="location"
                                type="text"
                               onChange={formik.handleChange}
                                 //value= {location1(formik.values.location)}

                                select

                                SelectProps={{
                                  native: true,
                                }}
                                style={{ marginRight: 1 }}
                                variant="filled"
                                fullWidth

                              >
                                {location.map((option) => (
                                  <option key={option.value} value={option.value}>
                                    {option.label}
                                  </option>
                                ))}

                              </TextField><br></br>
                              {formik.errors.location && formik.touched.location && (
                                <p>{formik.errors.location}</p>
                              )}
                            </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="type" className={classes.inputLabel} >Holiday Type:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="type"
                                name="type"
                                type="text"
                               onChange={formik.handleChange}
                               //value={hType(formik.values.type)}

                                select
                                style={{ marginRight: 1 }}

                                SelectProps={{
                                  native: true,
                                }}
                                variant="filled"
                                fullWidth

                              >
