import React,{useEffect,useState} from "react";
import { useFormik} from "formik";
import moment from 'moment';
 import {useParams} from "react-router-dom";
import * as Yup from "yup";
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
// import DeleteIcon from '@material-ui/icons/Delete';
import Link from "@material-ui/core/Link";
import {addTodo, readTodo, setHolidays} from '../slice/holidaySlice';
//import {setForID,setHolidays} from '../slice/holidaySlice';
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
// import moment from "moment";
import { holidayListSliceState,holidayListSliceId} from "../slice/holidaySlice";
import DeleteIcon from '@material-ui/icons/Delete';
import {formik} from '../component/formik';
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};
const hType =(code) =>{
  let desc1="Fixed"

  switch (code) {
    case "F":
        desc1="Fixed"
        break;
     case "O":
         desc1 ="Options"
         break;     
    }
  return desc1;
}

const location1 = (code) =>{
  let desc=  "All Locations";

  switch (code) {
     case "ALL":
         desc ="All Locations"
         break;
      case "IND":
          desc ="India"
          break;  
      case "USA":
         desc ="Bettendorf, IA"
         break;
       }
  return desc;
}

export default function App() {
  const classes = useStyles();
  const dispatch = useDispatch();

   const holidays = useSelector(holidayListSliceState);
   const holidaysListSliceId=useSelector(holidayListSliceId);
    console.log("holidaysListSliceId",holidaysListSliceId);
    const[APIData,setAPIData] =useState([]);
     useEffect(()=>{
        axios.get('/:actionType/:id')
        .then((res)=>{
          setAPIData(res.data)
        })
     },[])

    //const holidays =useSelector((state) => state.holidaySlice.holidays);
  //console.log("holidayData",holidayData);
//  const holidayData =useSelector(holidayListId);
//  console.log("holidayData",holidayData);
  //console.log("dataforId",dataforId );
    let  {id ,actionType}= useParams();



    useEffect(()=>{
    dispatch(readTodo(id))
  },[id])


  const formik = useFormik({
   initialValues: {
     id:"",
      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false

    },
   validationSchema: Yup.object({
      date:Yup.string().required("Required!").nullable(),
      location:Yup.string().required("Required!").nullable(),
      type:Yup.string().required("Required!").nullable(),
      description:Yup.string().required("Required!").nullable(),
    }),

    onSubmit:values => {

      console.log("values",values);
         dispatch(addTodo(values));
          // dispatch(readTodo(id));
        console.log("formikvalues1234",formik.values.date,
        formik.values.description,formik.values.id)
                //  moment((formik.values.date,'YYYY/mm/dd').format('mm/dd/YYYY')),
                // hType(formik.values.type),
                // location1(formik.values.location),
        //  )
         //dispatch(setForId(id));

       alert(JSON.stringify(values, null, 2));

     }})

  return (
    <div className="App">

         <form  onSubmit={formik.handleSubmit} >

                <Box display="flex" justifyContent="center" border={1} {...defaultProps}  px={2} pt={5} pb={4}>
                          <Grid container spacing={3}>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                              name="HolidayId"
                                id="outlined-full-width"
                                placeholder="#"
                               value={holidays.HolidayId}
                               onChange={formik.handleChange}
                               onBlur={formik.handleBlur}
                                style={{ margin: 1 }}

                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"
                                disabled
                              />
                              {formik.errors.HolidayId && formik.touched.HolidayId && (
                                <p>{formik.errors.HolidayId}</p>
                              )}
                            </Grid>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
