import React,{useEffect,useState} from "react";
import { useFormik,Form} from "formik";
import moment from 'moment';
 import {useParams} from "react-router-dom";
import * as Yup from "yup";
import { makeStyles, Typography } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import EditIcon from '@material-ui/icons/Edit';
import VisibilityIcon from '@material-ui/icons/Visibility';
import Link from "@material-ui/core/Link";
import {addTodo, readTodo, setHolidays} from '../slice/holidaySlice';
//import {setForID,setHolidays} from '../slice/holidaySlice';
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
// import moment from "moment";
import { holidayListSliceState,holidayListSliceId} from "../slice/holidaySlice";
import DeleteIcon from '@material-ui/icons/Delete';
import {useHistory} from "react-router-dom";
import axios from "axios";


const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),

  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};
const hType =(code) =>{
  let desc1="Fixed"

  switch (code) {
    case "F":
        desc1="Fixed"
        break;
     case "O":
         desc1 ="Options"
         break;     
    }
  return desc1;
}

const location1 = (code) =>{
  let desc=  "All Locations";

  switch (code) {
     case "ALL":
         desc ="All Locations"
         break;
      case "IND":
          desc ="India"
          break;  
      case "USA":
         desc ="Bettendorf, IA"
         break;
       }
  return desc;
}

export default function App() {
  const classes = useStyles();
  const dispatch = useDispatch();

   const holidays = useSelector(holidayListSliceState);

   const holidaysListSliceId=useSelector(holidayListSliceId);
  //  console.log("formikvalues1234",holidaysListSliceId.description);

    let  {id ,actionType}= useParams();
    console.log("actionType",actionType);


    useEffect(()=>{
     if(actionType === "view"){
    dispatch(readTodo(id))

     }

  },[id,actionType])
 //console.log("id",holidays.HolidayId)

  const formik = useFormik({
   initialValues: {
     id:"",
      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false

    },
   validationSchema: Yup.object({
      date:Yup.string().required("Required!").nullable(),
      location:Yup.string().required("Required!").nullable(),
      type:Yup.string().required("Required!").nullable(),
      description:Yup.string().required("Required!").nullable(),
    }),

    onSubmit:values => {

      console.log("values",values);
         dispatch(addTodo(values));
          // dispatch(readTodo(id));
        console.log("formikvalues1234",formik.values.date)
                //  moment((formik.values.date,'YYYY/mm/dd').format('mm/dd/YYYY')),
                // hType(formik.values.type),
                // location1(formik.values.location),
        //  )
         //dispatch(setForId(id));

       alert(JSON.stringify(values, null, 2));

     }})
//      const formikProps ={
//       intialvalues:intialValues
// }
console.log(holidaysListSliceId.HolidayId);
  return (

    <Box component="div" borderColor="primary-main" {...defaultProps} m={1}>
    <Grid container alignItems="center" spacing={1} style={{padding:1}}>
    <Grid item xs={2} style={{textAlign:"right"}}>HOLIDAY ID:</Grid>
    <Grid item xs={10} >
    <TextField varient="outlined" disabled={true}  placeholder="#" fullwidth />
    </Grid>
    <Grid item xs={2} style={{textAlign:"right"}}>Location:</Grid>
    <Grid item xs={10} >

    </Grid>                      


                              <Button>

                              </Button>

                              {(actionType !== "view") ?
                              <Button
                                  variant="contained"
                                  color="primary"
                                  className={classes.root}
                                  style={{ margin: '1 auto', display: "flex" }}
                                  type="submit"
                                  startIcon={<SaveIcon />}
                                 //onClick={()=>{history.push("/update/${id}")}}
                                >
                                  Save
                                </Button> : <div>
                                  </div>
                              }

                          {(actionType === "view") ?

                              <Button variant="outlined" 
                                 style={{ margin: '1 auto', display: "flex" }}
                                 color="primary"
                                 className={classes.root}
                                 startIcon={<EditIcon />}
                                 //onClick={()=>{history.push("/update/${id}")}}
                                 >Switch To Update
                                 </Button> : <div>
                                 </div>
                            }

                            {(actionType !== "view") && (actionType !== "create")?
                                 <Button
                                 variant="contained"
                                 color="secondary"
                                 className={classes.root}
                                 startIcon={<DeleteIcon />}
                                  href="/"
                                   >
                                  Delete
                                </Button> : <div></div>
                               }


                               {(actionType !=="view") && (actionType !=="create") ?
                               <Button variant="outlined" 
                               style={{ margin: '1 auto', display: "flex" }}
                               color="primary"
                               startIcon={<VisibilityIcon />}
                               className={classes.root}
                               //onClick={()=>{history.push("/view/${id}")}}
                               >Switch To View
                               </Button>

                              :<div>
                              </div>
                              }


                              <Button variant="outlined" 
                                 style={{ margin: '1 auto', display: "flex" }}
                                 color="primary"
                                 className={classes.root}
                                 >Goto:Holidays
                                 </Button>

                     </Grid>       

                 </Box>





  )}
