import React ,{useState,useEffect}from 'react';
import {useDispatch} from 'react-redux';
import { addTodo, holidayListSliceState } from "../slice/holidaySlice";
import { Formik} from 'formik';
import * as yup from 'yup';
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import Link from "@material-ui/core/Link";
import { useSelector } from "react-redux";


const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '60%',
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const Location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '55rem', height: '30rem' },
};

const INTIAL_FORM_STATE = {
  HOLIDAY_Id: '',
  DATE: '',
  Type: '',
  Location: '',
  Description: ''
};

const validationSchema = yup.object({

})


function Create() {
  const classes = useStyles();
  const dispatch =useDispatch();
  const holidays = useSelector(holidayListSliceState);
const [holiday,setHoliday] = useState({
     Date:"",
     Location:"",
     type:"",
     Description:"",
     isComplete:false
})
useEffect(() => {
  dispatch(addTodo(holiday));
}, [holiday]);

    const handleChange = (e) => {

      e.preventDefault()
      dispatch(addTodo(holiday))
      console.log(holiday)
      setHoliday({
        Date:"",
        Location:"",
        type:"",
        Description:"",
        isComplete:false
      })
  }
  return (

    <Grid Container>
      <Container>
        <Formik
          intialValue={{
            ...INTIAL_FORM_STATE
          }}
          validationSchema
          onSubmit={values => {
           console.log(values);
           }
          }
        >
          <Box display="flex" justifyContent="center" border={1} {...defaultProps} >
            <Grid container>

              <Grid item xs={6}>
                <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="outlined-full-width"
                  value="#"
                  style={{ margin: 1 }}

                  fullWidth
                  margin="normal"
                  InputLabelProps={{
                    shrink: true,
                  }}
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={6}>
                <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
              </Grid>
              <Grid item xs={6}>
               <TextField
                id="date"
                type="date"
                value={holiday.Date}
                style={{ margin: 1 }}
                format="MM/dd/yyyy"
                fullWidth
                margin="normal"
                InputLabelProps={{
                  shrink: true,
                }}
                variant="outlined"
                onChange={(e) =>setHoliday({...holiday,Date:e.target.value})}
              />


              </Grid>
              <Grid item xs={6}>
                <InputLabel htmlFor="Location" className={classes.inputLabel} >Location:</InputLabel>
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="outlined-full-width"
                  value={holiday.Location}
                  select
                  onChange={handleChange}
                  SelectProps={{
                    native: true,
                  }}
                  style={{ marginRight: 1 }}
                  variant="filled"
                  fullWidth
                  onChange={(e) =>setHoliday({...holiday,Location:e.target.value})}
                >
                  {Location.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}

                </TextField><br></br>
              </Grid>
              <Grid item xs={6}>
                <InputLabel htmlFor="Holiday Type" className={classes.inputLabel} >Holiday Type:</InputLabel>
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="outlined-full-width"
                  value={holiday.type}
                  select
                  style={{ marginRight: 1 }}
                  onChange={handleChange}
                  SelectProps={{
                    native: true,
                  }}
                  variant="filled"
                  fullWidth
                  onChange={(e) =>setHoliday({...holiday,type:e.target.value})}
                >
                  {type.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}

                </TextField>
              </Grid>
              <Grid item xs={6}>
                <InputLabel htmlFor="Description" className={classes.inputLabel} >Description:</InputLabel>
              </Grid>
              <Grid item xs={6}>
                <TextField
                  id="outlined-full-width"
                  value={holiday.Description}
                  style={{ marginRight: 1 }}
                  fullWidth
                  margin="normal"
                  InputLabelProps={{
                    shrink: true,
                  }}
                  variant="outlined"
                  onChange={(e) =>setHoliday({...holiday,Description:e.target.value})}
                />
              </Grid>

              <Grid item xs={12} sm={12} md={4} lg={4}
                style={{
                  textAlign: 'center'
                }}
              >
                <Button>

                </Button>
              </Grid>
              <Link href='/holiday'>
                <Button
                  variant="contained"
                  color="primary"
                  style={{ margin: '2 auto', display: "flex" }}
                  onClick={handleChange}
                  type="save"
                  startIcon={<SaveIcon />}
                >
                  Save
                </Button>
              </Link>
              <Link href='/'>
                <Button variant="contained" style={{ margin: '3 auto', display: "flex" }} >Goto:Holidays</Button>
                </Link>
             {/*<Link href='/holiday/:id'>
                <Button variant="contained" style={{ margin: '3 auto', display: "flex" }}>Switch To update</Button>
              </Link>
              <Link href='/holiday/:id'>
                <Button variant="contained" style={{ margin: '3 auto', display: "flex" }}>Delete</Button>
              </Link>
              <Link href='/holiday/:id'>
                <Button variant="contained" style={{ margin: '3 auto', display: "flex" }}>Switch to view</Button>
              </Link> */}

            </Grid>



          </Box>
        </Formik>
      </Container>
    </Grid>


  )
}


export default Create;
