import React from 'react';
import { Formik, Field, Form, FormikHelpers } from 'formik';
import * as yup from 'yup';
import { makeStyles, Typography } from '@material-ui/core';
import Grid from "@material-ui/core/Grid"; 
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import moment from "moment";
import { borders } from '@material-ui/system';
const  useStyles = makeStyles((theme) => ({
formWrapper :{
  marginTop:theme.spacing(5),
  marginBottom:theme.spacing(8),
},
imageIcon: {
  width: 170,
  marginright: "auto",
},
icon: {
  marginRight: theme.spacing(0.5),
},
Tool: {
  marginLeft: theme.spacing(60),
  paddingLeft: "14%",
},
underline: {
  borderBottom: "0px",
  "&:hover": {
      borderBottom: "0px ",
  },
  root: {
      paddingRight: theme.spacing(1),
  },
}}))

const INTIAL_FORM_STATE = {
   HOLIDAY_Id:'',
   DATE:'',
   Type:'',
   Location:'',
   Description:''
};
const validationSchema =yup.object({

})

function Create ()  {
   const classes = useStyles();
   const currentYear = moment().format("YYYY");
   const today = parseInt(currentYear);
   const [anchorEl, setAnchorEl] = React.useState(null);
   const handleClick = (event) => {
       setAnchorEl(event.currentTarget);
   };

   const handleClose = () => {
       setAnchorEl(null);
   };
   return(
    <Grid container>
     <Grid items xs={12}>
    </Grid>
    <Container>
      <div classname={classes.formWrapper}></div>
      <Formik 
       intialValue ={{
         ...INTIAL_FORM_STATE
       }}
       validationSchema
       onSubmit ={values => {
         console.log(values);
       }}
      >
      <Box display="flex" justifyContent="center">
      <Form>
        <label htmlFor="Holiday Id">Holiday Id:</label>
        <Field id="HolidayId" name="HolidayId" placeholder="#" />
        <label htmlFor="Date">Date:</label>
        <Field id="Date" name="Date" />
        <label htmlFor="Holiday Type">Holiday Type:</label>
        <Field id="Holiday Type" name="Holiday Type" />
        <label htmlFor="Location">Location:</label>
        <Field id="Location" name="Location" />
        <label htmlFor="Description">Description:</label>
        <Field id="Description" name="Description" />
        <button type="submit">Save</button>
        <button type="Goto holidays">Goto:holidays</button>
      </Form>
      </Box>
    </Formik>
    </Container>
    </Grid>

)}


export default Create;
