import React from 'react';
import { Formik, Field, Form} from 'formik';
import * as yup from 'yup';
import { makeStyles} from '@material-ui/core';
import Grid from "@material-ui/core/Grid"; 
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import NavBar from './NavBar';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import Link from "@material-ui/core/Link";
const  useStyles = makeStyles((theme) => ({
button: {
  margin: theme.spacing(1),
},
root: {
  justifyContent: 'center',
  pb:'3'
},
inputLabel:{
  paddingLeft:'60%',
},
textField:{
  margin:'7'
}
}))
const type = [
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const Location =[
{
label:'All Locations'
},
{
  label:'Bettendorf,IA'
},
{
  label:'India'
}
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '55rem', height: '30rem' },
};
const INTIAL_FORM_STATE = {
   HOLIDAY_Id:'',
   DATE:'',
   Type:'',
   Location:'',
   Description:''
};
const validationSchema =yup.object({

})

function Create ()  {
   const classes = useStyles();
   const [anchorEl, setAnchorEl] = React.useState(null);
   const [currency, setCurrency] = React.useState('');
   const [selectedDate, setSelectedDate] = React.useState(new Date('2014-08-18T21:11:54'));
   const handleDateChange = (date) => {
    setSelectedDate(date);
  };

const handleChange =() =>{

}
  return(

    <Grid Container>
    <Container>
   <Formik 
       intialValue ={{
         ...INTIAL_FORM_STATE
       }}
       validationSchema
       onSubmit ={values => {
         console.log(values);
       }}
      >
      <Box display="flex" justifyContent="center" border={1} {...defaultProps} >
     <Grid container>
     <form method="POST" action="/holidaypage" name="form1" id="form1"
    class="border border-primary pt-2 pb-2 pl-2 pr-2">
         <Grid item xs={6}>
         <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} nowrap>Holiday Id:</InputLabel>
         </Grid>
        <Grid item xs={6}>
        <TextField
        id="outlined-full-width"
        style={{ margin: 1 }}
        borderRight={3}
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        variant="outlined"
      />
         </Grid>

         <Grid item xs={6}>
         <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
         </Grid>
         <Grid item xs={6}>
         <TextField
         id="outlined-full-width"
         style={{ margin: 1 }}
         fullWidth
         margin="normal"
         InputLabelProps={{
           shrink: true,
         }}
         variant="outlined"
       />
        </Grid>
        <Grid item xs={6}>
        <InputLabel htmlFor="Holiday Type" className={classes.inputLabel} >Location:</InputLabel>
        </Grid>
        <Grid item xs={6}>
        <TextField
      id="outlined-full-width"
        select
        onChange={handleChange}
        SelectProps={{
          native: true,
        }}
        style={{ marginRight: 1}}
        variant="filled"
        fullWidth
      >
      {Location.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}

      </TextField><br></br>
      </Grid>
      <Grid item xs={6}>
        <InputLabel htmlFor="Holiday Type" className={classes.inputLabel} >Holiday Type:</InputLabel>
        </Grid>
        <Grid item xs={6}>
        <TextField
        id="outlined-full-width"
       select
       style={{ marginRight: 1}}
        onChange={handleChange}
        SelectProps={{
          native: true,
        }}
        variant="filled"
        fullWidth
      >
      {type.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}

      </TextField><br></br>
      </Grid>
      <Grid item xs={6}>
        <InputLabel htmlFor="Description" className={classes.inputLabel} >Description:</InputLabel>
        </Grid>
        <Grid item xs={6}>
        <TextField
        id="outlined-full-width"
        style={{ marginRight: 1}}
        fullWidth
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
        variant="outlined"
      />
        </Grid>

        <Grid item xs={12} sm={12} md={4} lg={4}
    style={{
        textAlign:'center' 
    }}
>
    <Button>

    </Button>
</Grid>
<Link>
<Button
variant="contained"
color="primary"
style={{margin: '2 auto', display: "flex"}}

startIcon={<SaveIcon />}
>
Save
</Button>
</Link>
    <Link href='/'>
     <Button variant="contained" style={{margin: '3 auto', display: "flex"}} >Goto:Holidays</Button>
     </Link>
     <Link href='/view/:id'>
     <Button variant="contained" style={{margin: '3 auto', display: "flex"}}>Switch To update</Button>
     </Link>
     </form>
     </Grid>

        </Box>
     </Formik>
    </Container>
    </Grid>


)}


export default Create;
