import React from 'react';
import { Formik, Field, Form} from 'formik';
import * as yup from 'yup';
import { makeStyles} from '@material-ui/core';
import Grid from "@material-ui/core/Grid"; 
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import NavBar from './NavBar';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import Link from "@material-ui/core/Link";
const  useStyles = makeStyles((theme) => ({
button: {
  margin: theme.spacing(1),
},
}))
const type = [
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const Location =[
{
label:'All Locations'
},
{
  label:'Bettendorf,IA'
},
{
  label:'India'
}
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 2,
  style: { width: '60rem', height: '30rem' },
};
const INTIAL_FORM_STATE = {
   HOLIDAY_Id:'',
   DATE:'',
   Type:'',
   Location:'',
   Description:''
};
const validationSchema =yup.object({

})

function Create ()  {
   const classes = useStyles();
   const [anchorEl, setAnchorEl] = React.useState(null);
   const [currency, setCurrency] = React.useState('');
   const [selectedDate, setSelectedDate] = React.useState(new Date('2014-08-18T21:11:54'));
   const handleDateChange = (date) => {
    setSelectedDate(date);
  };

const handleChange =() =>{

}
  return(

    <Grid container>
    <Container>
   <Formik 
       intialValue ={{
         ...INTIAL_FORM_STATE
       }}
       validationSchema
       onSubmit ={values => {
         console.log(values);
       }}
      >
      <Box display="flex" justifyContent="center" border={1} {...defaultProps} >
     <Grid container>
         <Grid item xs={6}>
         <InputLabel htmlFor="Holiday Id" varient="outlined" nowrap>Holiday Id:</InputLabel>
         </Grid>
        <Grid item xs={6}>
         <TextField id="outlined-basic" variant="outlined" />
         </Grid>

         <Grid item xs={6}>
         <InputLabel htmlFor="Date">Date:</InputLabel>
         </Grid>
         <Grid item xs={6}>
        <TextField id="outlined-basic"  variant="outlined" /><br></br>
        </Grid>
        <Grid item xs={6}>
        <InputLabel htmlFor="Holiday Type">Location:</InputLabel>
        </Grid>
        <Grid item xs={6}>
        <TextField

        id="filled-select-currency-native"
        select
        value={currency}
        onChange={handleChange}
        SelectProps={{
          native: true,
        }}

        variant="filled"
      >
      {Location.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}

      </TextField><br></br>
      </Grid>
      <Grid item xs={6}>
        <InputLabel htmlFor="Holiday Type">Holiday Type:</InputLabel>
        </Grid>
        <Grid item xs={6}>
        <TextField
        id="filled-select-currency-native"

        select

        value={currency}
        onChange={handleChange}
        SelectProps={{
          native: true,
        }}

        variant="filled"
      >
      {type.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}

      </TextField><br></br>
      </Grid>
      <Grid item xs={6}>
        <InputLabel htmlFor="Description">Description:</InputLabel>
        </Grid>
        <Grid item xs={6}>
        <TextField id="outlined-basic" variant="outlined" />
        </Grid>
        <Grid item xs={12}>
        <Button
        variant="contained"
        color="primary"
        size="large"
        className={classes.button}
        startIcon={<SaveIcon />}
      >
        Save
      </Button>
      <Button variant="contained">Goto:Holidays</Button>
      </Grid>
      <Link href='/view/:id'>
      <Button variant="contained" >Switch To update</Button>
      </Link>



        </Grid>
        </Box>
     </Formik>
    </Container>
    </Grid>


)}


export default Create;
