import React, { useEffect } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/paper';
import { useState } from 'react';
import Grid from '@material-ui/core/Grid';
import moment from 'moment';
import Box from '@material-ui/core/Box';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import { FormControl, IconButton } from '@material-ui/core';
import blue from '@material-ui/core/colors/blue';
import { useDispatch } from 'react-redux';
import { fetchUsers } from '../slice/holidaySlice';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import red from '@material-ui/core/colors/red';
const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const useStyles = makeStyles(theme => ({
  head: {
    background: '#endregion '
  },
  icon: {
    marginRight: theme.spacing(1),
    paddingTop:'0 px 1px'
  },
  table: {
    minwidth: '100%',
  },
  root: {
    marginLeft: theme.spacing(-1),
    marginRight: theme.spacing(-1)
  },
  menuButton: {
    width: '100%',
    color: 'white',
    backgroundColor: blue[500],
    fontFamily: "Arial",
  },
}));

export default function CustomizedTables() {
  const classes = useStyles();
  const currentYear = moment().format('YYYY');
  const today = parseInt(currentYear);
  const [year, setYear] = useState([]);
  const dispatch = useDispatch()
  //filter function
  const handleSearch = (event) => {
    const year = event.target.value
    dispatch(fetchUsers(year));
    if (year !== 0) {
      setYear(year)
    }
  }
  let result = [];
  for (let i = 0; i < 5; i++) {
    result[i] = today + i - 2;
  }
  useEffect(() => {
    dispatch(fetchUsers(year))
  }, [year, dispatch])
  return (
    <Box component="div" className={classes.root}>

      <Grid container spacing={1} className={classes.menuButton}>
      <IconButton>
      <AddCircleOutlineIcon style={{color:red[500]}} />
      </IconButton>
        <Grid item xs={1}></Grid>

        <Grid item>Filter-Year:</Grid>
        <Grid item >
          <FormControl>
            <Select onClick={handleSearch} defaultValue={today} style={{ color: 'black', backgroundColor: 'white', marginTop: '-3.5px' }}>
              {result.map(year => (
                <MenuItem key={year} value={year}>{year}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
      </Grid>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table" size="small">
          <TableHead>
            <TableRow >
              <StyledTableCell></StyledTableCell>
              <StyledTableCell >Date</StyledTableCell>
              <StyledTableCell >Description&nbsp;</StyledTableCell>
              <StyledTableCell >Type&nbsp;</StyledTableCell>
              <StyledTableCell >Location&nbsp;</StyledTableCell>
            </TableRow>
          </TableHead>
        </Table>
      </TableContainer>
    </Box>
  )
}
