        .then(todo =>{
            dispatch({
                type:"ADD_TODO",
                todo
            });
        })
        .catch(error =>{
            console.log(error.response)
        })
    }
}

export const holidayListSliceState = (state) => state.holidaySlice.holidayData;
export default holidaySlice.reducer;
