import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import moment from "moment";

//slice
const holidaySlice = createSlice({
    name: "holidayList",
    initialState: {
        holidayData: [],
    },
    reducers: {
        getHolidays(state, action) {
            state.holidayData = action.payload;
        },
        addTodo(state,action) {
            state.holidayData = action.payload;
        },
        readTodo(state,action){
            state.holidayData =action.payload;
        },
        updateTodo(state,action){
            state.holidayData =action.payload;
        },
        deleteTodo(state,action){
            state.holidayData =action.payload;
        },
    },
});
export const { getHolidays} = holidaySlice.actions;


//Actions
export const fetchUsers = (year) => async (dispatch) => {
    if (typeof year === "undefined") {
        year = moment().year();
    }
    axios.get(`http://localhost:4000/holiday/year/${year}`).then((res) => {
        dispatch(getHolidays(res.data));
    });

};
//post 
 export const addTodo = (holiday) => async(dispatch) => {

        axios.post(`http://localhost:4000/${holiday}`).then((res) =>{
            dispatch(addTodo(res.data))
        })

    };

//read the data
export const readTodo =(id) =>async(dispatch) => {
    axios.get(`http://localhost:4000/holiday/:${id}`).then((res) => {
        dispatch(readTodo(res.data))
    })
}
 //update
 export const updateTodo =(id) =>async(dispatch) =>{
     axios.put(`http://localhost:4000/holiday/:${id}`).then((res) =>{
         dispatch(updateTodo(res.data))
     })
 } 
 //delete
 export const deleteTodo =(id) =>async(dispatch) =>  {
     axios.delete(`http://localhost:4000/holiday/;${id}`).then((res) =>{
         dispatch(deleteTodo(res.data))
     })
 }

export const holidayListSliceState = (state) => state.holidaySlice.holidayData;
export default holidaySlice.reducer;
