import React from "react";
import {useFormik} from "formik";
import * as Yup from "yup";
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import Link from "@material-ui/core/Link";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const Location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};



export default function Form() {
  const classes = useStyles();
  const formik =useFormik({
    initialValues :{
      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false
      },
      validationSchema:Yup.object() ({
        date:Yup.string().required("this field is required").nullable(),
        location:Yup.string().required("this field is required").nullable(),
        type:Yup.string().required("this field is required").nullable(),
        description:Yup.string().required("this field is required").nullable(),
      }),
      onSubmit:values => {
        alert(JSON.stringify(values, null, 2));
      }
  });
  return (
    <form onSubmit={formik.handleSubmit}>
            <Box display="flex" justifyContent="center" border={1} {...defaultProps}  px={2} pt={5} pb={4}>
                      <Grid container spacing={3}>

                        <Grid item xs={2}>
                          <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
                        </Grid>
                        <Grid item xs={10}>
                          <TextField
                          name="HolidayId"
                            id="outlined-full-width"
                            value="#"
                            style={{ margin: 1 }}

                            fullWidth
                            margin="normal"
                            InputLabelProps={{
                              shrink: true,
                            }}
                            variant="outlined"
                          />
                        </Grid>

                        <Grid item xs={2}>
                          <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
                        </Grid>
                        <Grid item xs={10}>
                         <TextField
                          id="date"
                          name="date"
                          type="date"
                          onChange={formik.handleChange}
                           value={formik.values.date}

                          style={{ margin: 1 }}
                          format="MM/dd/yyyy"
                          fullWidth
                          margin="normal"
                          InputLabelProps={{
                            shrink: true,
                          }}
                          variant="outlined"
                        />


                        </Grid>
                        <Grid item xs={2}>
                          <InputLabel htmlFor="location" className={classes.inputLabel} >Location:</InputLabel>
                        </Grid>
                        <Grid item xs={10}>
                          <TextField
                            id="location"
                            name="location"
                            type="text"
                           onChange={formik.handleChange}
                           value={formik.values.location}
                            select

                            SelectProps={{
                              native: true,
                            }}
                            style={{ marginRight: 1 }}
                            variant="filled"
                            fullWidth

                          >
                            {Location.map((option) => (
                              <option key={option.value} value={option.value}>
                                {option.label}
                              </option>
                            ))}

                          </TextField><br></br>
                        </Grid>
                        <Grid item xs={2}>
                          <InputLabel htmlFor="type" className={classes.inputLabel} >Holiday Type:</InputLabel>
                        </Grid>
                        <Grid item xs={10}>
                          <TextField
                            id="type"
                            name="type"
                            type="text"
                           onChange={formik.handleChange}
                           value={formik.values.type}
                            select
                            style={{ marginRight: 1 }}

                            SelectProps={{
                              native: true,
                            }}
                            variant="filled"
                            fullWidth

                          >
                            {type.map((option) => (
                              <option key={option.value} value={option.value}>
                                {option.label}
                              </option>
                            ))}

                          </TextField>
                        </Grid>
                        <Grid item xs={2}>
                          <InputLabel htmlFor="description" className={classes.inputLabel} >Description:</InputLabel>
                        </Grid>
                        <Grid item xs={10}>
                          <TextField
                            id="description"
                            name="desciption"
                            type="text"
                            onChange={formik.handleChange}
                            values={formik.values.description}

                            style={{ marginRight: 1 }}
                            fullWidth
                            margin="normal"
                            InputLabelProps={{
                              shrink: true,
                            }}
                            variant="outlined"

                          />

                        </Grid>

                        <Grid item xs={12} sm={12} md={4} lg={4}
                          style={{
                            textAlign: 'center'
                          }}
                        >
                          <Button>

                          </Button>
                        </Grid>
                        <Link href='/holiday'>
                          <Button
                            variant="contained"
                            color="primary"
                            style={{ margin: '2 auto', display: "flex" }}

                            type="submit"
                            startIcon={<SaveIcon />}
                          >
                            Save
                          </Button>
                        </Link>
                        <Link href='/'>
                          <Button variant="contained" style={{ margin: '3 auto', display: "flex" }} >Goto:Holidays</Button>
                          </Link>
                       {/*<Link href='/holiday/:id'>
                          <Button variant="contained" style={{ margin: '3 auto', display: "flex" }}>Switch To update</Button>
                        </Link>
                        <Link href='/holiday/:id'>
                          <Button variant="contained" style={{ margin: '3 auto', display: "flex" }}>Delete</Button>
                        </Link>
                        <Link href='/holiday/:id'>
                          <Button variant="contained" style={{ margin: '3 auto', display: "flex" }}>Switch to view</Button>
                        </Link> */}

                     </Grid>

               </Box>
              </form>
           )}
