import React,{ useEffect ,useState}  from "react";
import {useFormik} from "formik";
import * as Yup from "yup";
import { makeStyles } from '@material-ui/core';
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import DeleteIcon from '@material-ui/icons/Delete';
import Link from "@material-ui/core/Link";
import {addTodo, holidayListSliceState} from '../slice/holidaySlice';
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
  root: {
    justifyContent: 'center',
    pb: '3'
  },
  inputLabel: {
    paddingLeft: '30%',    
  },
  textField: {
    margin: '7'
  }
}))
const type = [
  {
    label:'Select',
  },
  {

    label: 'Optional',
  },
  {

    label: 'Fixed',
  },
]
const location = [
  {
    label:'Select',
  },
  {
    label: 'All Locations'
  },
  {
    label: 'Bettendorf,IA'
  },
  {
    label: 'India'
  }
]
const defaultProps = {
  bgcolor: 'background.paper',
  m: 1,
  border: 1,
  style: { width: '65rem', height: '35rem' },
};
// const initialValues= {

//   date:'',
//   location:'',
//   type:'',
//   description:'',
//   isComplete:false

// },
// const todos =(state=[],action) =>{
//   switch(action.type){
//     case 'ADD_TODO':
//       return(
//         [
//           ...state,
//           todo(

//           )
//         ]
//       )

//       ]
//   }
// }
export default function App() {
  const classes = useStyles();
  const [todo,setTodo] = useState({
    date:"",
    description:"",
    type:"",
    location:"",
    isComplete:false

  })
  // const [initialValues,setInitialValues] = useState(initialValues);
  const holidays = useSelector(holidayListSliceState);
  const dispatch = useDispatch();

  const [date,setDate] =useState("");
  const[description,setDescription] =useState("");
  const[type,setType] =useState("");
  const[location,setLocation] =useState("");

  const handleDate =(e) =>setDate(e.target.value);
  const handleDescription =(e) =>setDescription(e.target.value);
  const handleType =(e) =>setType(e.target.value);
  const handleLocation =(e) =>setLocation(e.target.value);

  // const handleClick =() =>{
  //   if(date && description && type && location) {
  //     dispatch(
  //       addTodo({

  //         date,
  //         description,
  //         type,
  //         location,
  //       })
  //     );
  //     setDate("");
  //     setDescription("");
  //     setType("");
  //     setLocation("");

  //   }
  // }
  const formik = useFormik({
   initialValues: {

      date:'',
      location:'',
      type:'',
      description:'',
      isComplete:false

    },
   validationSchema: Yup.object({
      date:Yup.string().required("Required!"),
      location:Yup.string().required("Required!").nullable(),
      type:Yup.string().required("Required!").nullable(),
      description:Yup.string().required("Required!").nullable(),
    }),
    onSubmit: (values) => {
      console.log(values);
       alert(JSON.stringify(values, null, 2));

    },

  })
//   useEffect((props) => {
//     dispatch(addTodo());
// }, []);
// const formikProps ={
//       intialvalues:intialValues,

// }
  return (
    <div>

    <form onSubmit={formik.handleSubmit}>
                <Box display="flex" justifyContent="center" border={1} {...defaultProps}  px={2} pt={5} pb={4}>
                          <Grid container spacing={3}>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Holiday Id" margin="dense" varient="outlined" className={classes.inputLabel} >Holiday Id:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                              name="HolidayId"
                                id="outlined-full-width"
                                value="#"
                                style={{ margin: 1 }}

                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"
                              />
                            </Grid>

                            <Grid item xs={2}>
                              <InputLabel htmlFor="Date" className={classes.inputLabel} >Date:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                             <TextField
                              id="date"
                              name="date"
                              type="date"
                              onChange={formik.handleChange}
                              //  value={formik.values.date}
                              value={todo.date}
                              style={{ margin: 1 }}
                              format="MM/dd/yyyy"
                              fullWidth
                              margin="normal"
                              InputLabelProps={{
                                shrink: true,
                              }}
                              variant="outlined"
                            />
                            {formik.errors.date && formik.touched.date && (
                              <p>{formik.errors.date}</p>
                            )}
                           </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="location" className={classes.inputLabel} >Location:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="location"
                                name="location"
                                type="text"
                               onChange={formik.handleChange}
                              //  value={formik.values.location}
                              value={todo.location}
                                select

                                SelectProps={{
                                  native: true,
                                }}
                                style={{ marginRight: 1 }}
                                variant="filled"
                                fullWidth

                              >
                                {location.map((option) => (
                                  <option key={option.value} value={option.value}>
                                    {option.label}
                                  </option>
                                ))}

                              </TextField><br></br>
                              {formik.errors.location && formik.touched.location && (
                                <p>{formik.errors.location}</p>
                              )}
                            </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="type" className={classes.inputLabel} >Holiday Type:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="type"
                                name="type"
                                type="text"
                               onChange={formik.handleChange}
                              //  value={formik.values.type}
                              value={todo.type}
                                select
                                style={{ marginRight: 1 }}

                                SelectProps={{
                                  native: true,
                                }}
                                variant="filled"
                                fullWidth

                              >
                                {type.map((option) => (
                                  <option key={option.value} value={option.value}>
                                    {option.label}
                                  </option>
                                ))}

                              </TextField>
                              {formik.errors.type && formik.touched.type&& (
                                <p>{formik.errors.type}</p>
                              )}
                            </Grid>
                            <Grid item xs={2}>
                              <InputLabel htmlFor="description" className={classes.inputLabel} >Description:</InputLabel>
                            </Grid>
                            <Grid item xs={10}>
                              <TextField
                                id="description"
                                name="description"
                                type="text"
                                onChange={formik.handleChange}
                                // values={formik.values.description}
                               values={todo.description}
                                style={{ marginRight: 1 }}
                                fullWidth
                                margin="normal"
                                InputLabelProps={{
                                  shrink: true,
                                }}
                                variant="outlined"

                              />
                              {formik.errors.description && formik.touched.description && (
                                <p>{formik.errors.description}</p>
                              )}
                            </Grid>

                            <Grid item xs={12} sm={12} md={4} lg={4}
                              style={{
                                textAlign: 'center'
                              }}
                            >
                              <Button>

                              </Button>
                            </Grid>
                            <Link href='/holiday'>
                              <Button
                                variant="contained"
                                color="primary"
                                style={{ margin: '2 auto', display: "flex" }}
                                type="submit"
                                startIcon={<SaveIcon />}
                              >
                                Save
                              </Button>
                            </Link>
                            <Link href='/'>
                              <Button variant="contained" style={{ margin: '3 auto', display: "flex" }} >Goto:Holidays</Button>
                              </Link>
                           {/*<Link href='/holiday/:id'>
                              <Button variant="contained"  color="primary" style={{ margin: '3 auto', display: "flex" }}>Switch To update</Button>
                            </Link>
                           <Button
                              variant="contained"
                              color="secondary"

                              startIcon={<DeleteIcon />}
                                >
                               Delete
                             </Button>
                            <Link href='/holiday/:id'>
                              <Button variant="contained" color="primary" style={{ margin: '3 auto', display: "flex"}}>Switch to view</Button>
                            </Link> */}

                         </Grid>

                   </Box>
                  </form>
                  </div>
               )
       }
