import React from 'react';
 import { Formik } from 'formik';
 import HolidayPage from '../component/HolidayPage';

 const HolidayFormik = () => (
   <div>
     <Formik
       initialValues={{ 
           id:"",
           date:"",
           type:"",
           type:"",
           location:""
        }}
       onSubmit={(values, actions) => {
         setTimeout(() => {
           alert(JSON.stringify(values, null, 2));
           actions.setSubmitting(false);
         }, 1000);
       }}
     >
       {props => (
         <form onSubmit={props.handleSubmit}>
         <Box component="div" borderColor="primary.main" {...defaultProps} m={1}>
         <Grid container alignItems="center" spacing={3} style={{ padding: 7 }}>
           <Grid item xs={2} style={{ textAlign: "right" }}>Holiday Id:</Grid>
           <Grid item xs={10} >
             <TextField
               id="full-width-text-field"
               variant="outlined"
               placeholder="#"
               margin="normal"

               fullWidth
             />

           </Grid>
           <Grid item xs={2} style={{ textAlign: "right" }}>Date:</Grid>
           <Grid item xs={10}>
             <TextField
               id="full-width-text-field"
               variant="outlined"
               type="date"
               defaultValue=""
               //className={classes.textField}
               fullwidth
               style={{ width: 850 }}
               InputLabelProps={{
                 shrink: true,
               }} />

           </Grid>
           <Grid item xs={2} style={{ textAlign: "right" }}>Location:</Grid>
           <Grid item xs={10} >
             <Autocomplete
               id="combo-box-demo"
               options={location}
               getOptionLabel={(option) => option.title}
               style={{ width: 850 }}
               renderInput={(params) => <TextField {...params} label="Select" variant="outlined" />}
             />
           </Grid>
           <Grid item xs={2} style={{ textAlign: "right" }}>Holiday Type:</Grid>
           <Grid item xs={10} >
             <Autocomplete
               id="combo-box-demo"
               label="select"
               options={type}
               getOptionLabel={(option) => option.title}
               style={{ width: 850 }}

               renderInput={(params) => <TextField {...params} label="Select" variant="outlined" />}
             />
           </Grid>
           <Grid item xs={2} style={{ textAlign: "right" }}  >Description:</Grid>
           <Grid item xs={10} >
             <TextField
               id="full-width-text-field"
               variant="outlined"
               margin="normal"
               fullWidth
             />


             <Grid container mb="5" >

               <Link>
                 {(actionType !== "view") ?
                   <Button
                     variant="contained"
                     color="primary"
                     className={classes.root}
                     style={{ margin: '1 auto', display: "flex" }}
                     type="submit"
                     startIcon={<SaveIcon />}
                     onClick={() => { history.push("/update/${id}") }}
                   >
                     Save
                                   </Button> : <div>
                   </div>
                 }
               </Link>

               <Link>
                 {(actionType === "view") ?

                   <Button variant="outlined"
                     style={{ margin: '1 auto', display: "flex" }}
                     color="primary"
                     className={classes.root}
                     startIcon={<EditIcon />}
                     onClick={() => { history.push("/update/${id}") }}
                   >Switch To Update
                                    </Button> : <div>
                   </div>
                 }
               </Link>
               <Link>
                 {(actionType !== "view") && (actionType !== "create") ?
                   <Button
                     variant="contained"
                     color="secondary"
                     className={classes.root}
                     startIcon={<DeleteIcon />}
                     href="/"
                   >
                     Delete
                                   </Button> : <div></div>
                 }
               </Link>
               <Link>
                 {(actionType !== "view") && (actionType !== "create") ?
                   <Button variant="outlined"
                     style={{ margin: '1 auto', display: "flex" }}
                     color="primary"
                     startIcon={<VisibilityIcon />}
                     className={classes.root}
                     onClick={() => { history.push("/view/${id}") }}
                   >Switch To View
                                  </Button>

                   : <div>
                   </div>
                 }
               </Link>
               <Link href="/">
                 <Button variant="outlined"
                   style={{ margin: '1 auto', display: "flex" }}
                   color="primary"
                   className={classes.root}
                 >Goto:Holidays
                                    </Button>
               </Link>

             </Grid>
           </Grid>

         </Grid></Box>

         </form>
       )}
     </Formik>
   </div>
 );

 export default HolidayFormik;
